// get access token from local storagr
// get user basic information  from the spotify api using axios

import axios from 'axios' 
import React from 'react'
import './Homepage.css'




const Homepage = () => {

    localStorage.getItem('access_token');
    const [user, setUser] = React.useState({
        name: '',
        email: '',
        image: '',
        country: '',
        id: ''
    })

    React.useEffect(() => {
        const getUser = async () => {
            const token = localStorage.getItem('access_token');
            const user = await axios.get('https://api.spotify.com/v1/me', {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })
            setUser(user.data)
        }
        getUser()
    }, [])

    return (
        <div className='Homepage'>
            <div className='Homepage-user'>
                <img className='ProfileImage' src={user.images[0].url} alt='user' />
                <div className='Homepage-user-info'>
                    <h2>{user.display_name}</h2>
                    <p>{user.email}</p>
                    <p>{user.country}</p>
                </div>
            </div>
            <div className='Homepage-playlists'>
                <h2>Playlists</h2>
                <div className='Homepage-playlists-list'>
                    <div className='Homepage-playlists-list-item'>
                        <img src='https://i.scdn.co/image/ab67616d0000b273f8f9b9b9b9b9b9b9b9b9b9b9b9' alt='playlist' />
                        <p>Playlist 1</p>
                    </div>
                    <div className='Homepage-playlists-list-item'>
                        <img src='https://i.scdn.co/image/ab67616d0000b273f8f9b9b9b9b9b9b9b9b9b9b9b9' alt='playlist' />
                        <p>Playlist 2</p>
                    </div>
                    <div className='Homepage-playlists-list-item'>
                        <img src='https://i.scdn.co/image/ab67616d0000b273f8f9b9b9b9b9b9b9b9b9b9b9b9' alt='playlist' />
                        <p>Playlist 3</p>
                    </div>
                </div>
            </div>
        </div>
    )
}


export default Homepage
