// get the user playlists and show them on a list with export button
// export button export the playlist to a json file
// this function won't have a search bar
// it simply shows the user's playlists

import React, { useState, useEffect } from 'react';
import './Playlists.css';
import { Link } from 'react-router-dom';
import axios from 'axios'










const Playlists = () => {

    const [playlists, setPlaylists] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const token = localStorage.getItem('access_token');




    const exportPlaylist = (playlist) => {
        console.log(playlist.target);
        // const playlistName = playlist.name;
        // const playlistTracks = playlist.tracks.items;
        // const playlistTracksArray = [];
        // playlistTracks.forEach(track => {
        //     playlistTracksArray.push(track.track.name);
        // });
        // const playlistTracksString = playlistTracksArray.join(', ');
        // const playlistString = `${playlistName} - ${playlistTracksString}`;
        // const playlistJSON = JSON.stringify(playlistString);
        // const blob = new Blob([playlistJSON], { type: 'application/json' });
        // const url = URL.createObjectURL(blob);
        // const a = document.createElement('a');
        // a.href = url;
        // a.download = `${playlistName}.json`;
        // a.click();
        // URL.revokeObjectURL(url);
    }



    useEffect(() => {

        if (token) {
            axios.get('https://api.spotify.com/v1/me/playlists', {
                headers: {
                    'Authorization': 'Bearer ' + token
                }
            })
            .then(response => {
                setPlaylists(response.data.items);
                console.log(response.data.items)
                setLoading(false);
            })
            .catch(error => {
                setError(error);
                setLoading(false);
            });
        }
    }
    , [])

    if (loading) {
        return <div>Loading...</div>
    }

    if (error) {
        return <div>Error: {error.message}</div>
    }

    if (!playlists && !token) {
        return <div>Not logged in</div>
    }


    return (
        <div className='Playlists'>
            <div className='Playlists-header'>
                <h1>Playlists</h1>
            </div>
            <div className='Playlists-list'>
                {playlists.map(playlist => (
                    <div className='Playlists-list-item' key={playlist.id}>
                        <div className='Playlists-list-item-image'>
                            <img src={playlist.images[0].url} alt='Playlist' />
                        </div>
                        <div className='Playlists-list-item-info'>
                            <div className='Playlists-list-item-name'>{playlist.name}</div>
                            <div className='Playlists-list-item-tracks'>{playlist.tracks.total} tracks</div>
                        </div>
                        <div className='Playlists-list-item-export'>
                            <button key={playlist.id} onClick={exportPlaylist} >Export</button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    )
}

export default Playlists;
