// get access token from local storagr
// get user basic information  from the spotify api using axios
// show a loading screen until the user data is loaded
// show the user data
// show the user's profile picture if it exists
// or show defaultImage
import axios from 'axios' 
import React from 'react'
import Playlists from '../Playlists/Playlists'
import './Homepage.css'
import Profile from './Profile.jpg'

const Homepage = () => {
    const [user, setUser] = React.useState(null)
    const [loading, setLoading] = React.useState(true)
    const [error, setError] = React.useState(null)
    const token = localStorage.getItem('access_token')

    React.useEffect(() => {
        if (token) {
            axios.get('https://api.spotify.com/v1/me', {
                headers: {
                    'Authorization': 'Bearer ' + token
                }
            })
            .then(response => {
                setUser(response.data)
                // Convert response.data to string and save it on local storage
                localStorage.setItem('user', JSON.stringify(response.data))
                setLoading(false)
            })
            .catch(error => {
                setError(error)
                setLoading(false)
            })
        }
    }, [token])

    if (loading) {
        return <div>Loading...</div>
    }

    if (error) {
        return <div>Error: {error.message}</div>
    }

    if (!user) {
        return <div>Not logged in</div>
    }

    return (
        <div className='Homepage'>
            <div className='Homepage-user'>
                <img src={user.images.length ? user.images[0].url : Profile} alt='Profile' />
                <div className='Homepage-user-info'>
                    <div className='Homepage-user-name'>{user.display_name}</div>
                    <div className='Homepage-user-followers'>{user.followers.total} followers</div>

                    <div className='Homepage-user-email'>{user.email}</div>
                </div>
            </div>
            <Playlists />
        </div>

    )
}

export default Homepage;




