// get access token from local storagr
// get user basic information  from the spotify api using axios

import axios from 'axios' 
import React from 'react'
import './Homepage.css'
import Profile from './Profile.jpg'

const Homepage = () => {
    const defaultImage = {Profile}
    localStorage.getItem('access_token');
    const [user, setUser] = React.useState({})

    React.useEffect(() => {
        const getUser = async () => {
            const token = localStorage.getItem('access_token');
            const user = await axios.get('https://api.spotify.com/v1/me', {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })
            setUser(user.data)
            console.log(user.data)
        }
        getUser()
    }, [])



    return (
    <div>

        <div className='Homepage'>
            <div className='Homepage-header'>
                {/* if user.images.length = 0 image will have the defaultImage else if the user.images.length > 0*/}
                {/* <img src={user.images.length < 0 ?  defaultImage} alt='user' className='Homepage-image'/> */}
                <h1>{user.display_name}</h1>
                <h2>{user.email}</h2>
                <h3>{user.country}</h3>
            </div>




            </div>

    </div>
  )
}

export default Homepage
