// get access token from local storagr
// get user basic information  from the spotify api using axios

import axios from 'axios' 
import React from 'react'
import './Homepage.css'




const Homepage = () => {

    localStorage.getItem('access_token');
    const [user, setUser] = React.useState({
        name: '',
        email: '',
        image: '',
        country: '',
        id: ''
    })

    React.useEffect(() => {
        // Fix this Error
        // Access to XMLHttpRequest at 'https://api.spotify.com/v1/me' from origin 'http://localhost:3000' has been blocked by CORS policy: Request header field access-control-allow-origin is not allowed by Access-Control-Allow-Headers in preflight response.

        axios.get('https://api.spotify.com/v1/me', {
            headers: {
                'Authorization': 'Bearer ' + localStorage.getItem('access_token')

            }
        })

            .then(res => {
                if (res.data.images === undefined)
                setUser({
                    name: res.data.display_name,
                    email: res.data.email,
                    image: res.data.images[0].url,
                    country: res.data.country,
                    id: res.data.id
                })
            })
    }, [])

    return (
    <div>

        <div className='Homepage'>
            <div className='Homepage-header'>
                <img src={user.image} alt='user' className='ProfileImage' />
                <h1>{user.name}</h1>
                <h2>{user.email}</h2>
                <h3>{user.country}</h3>
            </div>

            </div>

    </div>
  )
}

export default Homepage
