// Search bar, Quiries the spotify api to find the song, album, artist, playlist, and user
// has a dropdown menu to select one of the result

import React from 'react';
import './Navbar.css';
import { Link } from 'react-router-dom';
import { useState } from 'react';
import { useEffect } from 'react';
import { useRef } from 'react';
import { useCallback } from 'react';
import { useMemo } from 'react';


const Navbar = () => {

    const [search, setSearch] = useState('');
    const [results, setResults] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const token = localStorage.getItem('access_token');
    const searchRef = useRef(null);

    const handleSubmit = useCallback(
        (event) => {
            event.preventDefault();
            setLoading(true);
            setError(null);
            setResults([]);
            axios.get(`https://api.spotify.com/v1/search?q=${search}&type=track,artist,album,playlist,user`, {
                headers: {
                    'Authorization': 'Bearer ' + token
                }
            })
                .then(response => {
                    setResults(response.data.tracks.items);
                    setLoading(false);
                })
                .catch(error => {
                    setError(error);
                    setLoading(false);
                });
        },
        [search, token]
    );

    useEffect(() => {
        if (searchRef.current) {
            searchRef.current.focus();
        }
    }, [searchRef]);

    const handleChange = useCallback(
        (event) => {
            setSearch(event.target.value);
        },
        [setSearch]
    );

    const handleKeyPress = useCallback(
        (event) => {
            if (event.key === 'Enter') {
                handleSubmit(event);
            }
        },
        [handleSubmit]
    );

    const handleClick = useCallback(
        (item) => {
            setSearch(item.name);
            setResults([]);
        },
        [setSearch, setResults]
    );

    const resultsList = useMemo(
        () => {
            if (loading) {
                return <div>Loading...</div>;
            }
            if (error) {
                return <div>Error: {error.message}</div>;
            }
            if (results.length === 0) {
                return <div>No results found</div>;
            }
            return (
                <ul className="Navbar-results">
                    {results.map(item => (
                        <li key={item.id} onClick={() => handleClick(item)}>
                            <img src={item.album.images.length ? item.album.images[0].url : defaultImage} alt={item.name} />
                            <div className="Navbar-results-info">
                                <div className="Navbar-results-name">{item.name}</div>
                                <div className="Navbar-results-type">{item.type}</div>
                            </div>
                        </li>
                    ))}
                </ul>
            );
        },
        [loading, error, results, handleClick]
    );
}

export default Navbar
