// Using the spotify Api to get the user's data
// https://developer.spotify.com/documentation/web-api/reference/
// Save the access token to local storage
// Client ID: Client ID a724daf951794ec2bfef5d65d2ec9a34


import React from 'react'
import './Login.css'


const Login = () => {

    return (
    <div className='Login'>
        {/* Login button redirect to App */}
        <label htmlFor='login'>Login with your Spotify account</label>
        <button id='login' className='login-button' onClick={() => {
            window.location.href = 'https://accounts.spotify.com/authorize?client_id=f9f8f8f8f8f8f8f8f8f8f8f8f8f8f8f8&response_type=code&redirect_uri=http://localhost:3000/callback&scope=user-read-private%20user-read-email&show_dialog=true'
        }}>Login</button>
    </div>
  )
}

export default Login
